        if (i == start) {
          if (ch == '\'') { replace(i, i + 1, '"'); }
        } else {
          if (i + 1 == end) {
            char startDelim = jsonish.charAt(start);
            if (startDelim!= '\'') {
              startDelim = '"';
            }
            closed = startDelim == ch;
          }
          if (closed) {
            if (ch == '\'') { replace(i, i + 1, '"'); }
          } else if (ch == '"') {
            insert(i, '\\');
          }
        }
        break;
    }
  }
}

private void sanitizeString(int start, int end) {
  boolean closed = false;
  for (int i = start; i < end; ++i) {
    char ch = jsonish.charAt(i);
    switch (ch) {
      case '\n': replace(i, i + 1, "\\n"); break;
      case '\r': replace(i, i + 1, "\\r"); break;
      case '\u2028': replace(i, i + 1, "\\u2028"); break;
      case '\u2029': replace(i, i + 1, "\\u2029"); break;
      case '"': case '\'':
        if (i == start) {
          if (ch == '\'') { replace(i, i + 1, '"'); }
        } else {
          if (i + 1 == end) {
            char startDelim = jsonish.charAt(start);
            if (startDelim!= '\'') {
              startDelim = '"';
            }
            closed = startDelim == ch;
          }
          if (closed) {
            if (ch == '\'') { replace(i, i + 1, '"'); }
          } else if (ch == '"') {
            insert(i, '\\');
          }
        }
        break;
      case '\\': replace(i, i + 1, "\\\\"); break;
      case '/': replace(i, i + 1, "\\/"); break;
      case 'b': replace(i, i + 1, "\\b"); break;
      case 'f': replace(i, i + 1, "\\f"); break;
      case 'n': replace(i, i + 1, "\\n"); break;
      case 'r': replace(i, i + 1, "\\r"); break;
      case 't': replace(i, i + 1, "\\t"); break;
      case 'u2028': replace(i, i + 1, "\\u2028"); break;
      case 'u2029': replace(i, i + 1, "\\u2029"); break;
      default: break;
    }
  }
}
